import{_ as e,f as t,h as o,aq as i}from"./vue-router.esm-bundler-0d52b99f.js";const p={components:{}},r={class:"varlet-site-doc"},a=i('<h1>Varlet VSCode Extension</h1><div class="card"><h3>Intro</h3><p>For a better development experience, we built a <code>VSCode plugin</code>.</p></div><div class="card"><h3>Features</h3><ul><li>🛠️  Support syntax hints and quick completion for all components.</li><li>🛠️  Support document address preview and quick jump for all components.</li><li>🛠️  Support frame selection of code snippets to quickly open <code>Playground</code>.</li><li>🛠️  Support full icon preview for icon components.</li><li>🌍  Support switching between Chinese and English.</li></ul></div><div class="card"><h3>Install</h3><p>VsCode Extension Marketplace Search <a href="https://marketplace.visualstudio.com/items?itemName=haoziqaq.varlet-vscode-extension">varlet-vscode-extension</a>.</p></div><div class="card"><h3>How To Use</h3><p>Grammatical hints appear when you enter the keyword of the component name, and you can quickly complete it after selecting it.</p><img src="https://varlet.gitee.io/varlet-ui/vscode/1.png" style="width:100%;display:block;border-radius:4px;margin-top:16px;"><p>Moving the mouse to the component name will display the document address of the component, which can be clicked to jump.</p><img src="https://varlet.gitee.io/varlet-ui/vscode/2.png" style="width:100%;display:block;border-radius:4px;margin-top:16px;"><p>Press space or attribute keywords in the attribute input range of the label to display attribute completion prompts and attribute information.</p><img src="https://varlet.gitee.io/varlet-ui/vscode/3.png" style="width:100%;display:block;border-radius:4px;margin-top:16px;"><p>When the <code>name</code> property of the <code>Icon</code> component is input, an icon preview will appear, and syntax completion will be performed after selection.</p><img src="https://varlet.gitee.io/varlet-ui/vscode/4.png" style="width:100%;display:block;border-radius:4px;margin-top:16px;"><p>Frame the code snippet, and you can quickly open <code>Playground</code> in the right-click menu to preview the framed code online.</p><img src="https://varlet.gitee.io/varlet-ui/vscode/7.png" style="width:100%;display:block;border-radius:4px;margin-top:16px;"><p>Support switching between Chinese and English, more friendly to Chinese people.</p><img src="https://varlet.gitee.io/varlet-ui/vscode/5.png" style="width:100%;display:block;border-radius:4px;margin-top:16px;"><img src="https://varlet.gitee.io/varlet-ui/vscode/6.png" style="width:100%;display:block;border-radius:4px;margin-top:10px;"></div>',5),n=[a];function s(c,l,d,h,m,u){return t(),o("div",r,n)}const g=e(p,[["render",s]]);export{g as default};
